import React from 'react';
import { Provider } from 'react-redux';
import { persistStore } from 'redux-persist';
import crosstabSync from 'redux-persist-crosstab';
import clearStorage from './clearStorage';

export default ({ store, Router, persistWhitelist, persistBlacklist, placeholdingComponent = null, clearStorageArgs = {} }) => {
    clearStorage(clearStorageArgs);

    class App extends React.Component {
        constructor() {
            super();
            this.state = { rehydrated: false };
        }

        componentWillMount() {
            const options = { whitelist: persistWhitelist, blacklist: persistBlacklist };
            const persistor = persistStore(store, options, () => this.setState({ rehydrated: true }));
            crosstabSync(persistor, options);
        }

        render() {
            if(!this.state.rehydrated)
                return placeholdingComponent;

            return (
                <Provider store={store}>
                    <Router />
                </Provider>
            );
        }
    }

    return App;
};